package Bean;

import java.util.Date;

public class Note {
	
private int code;
private int cc;
private int cf;



public Note() {

	// TODO Auto-generated constructor stub
}



public Note(int code, int cc, int cf) {
	super();
	this.code = code;
	this.cc = cc;
	this.cf = cf;
}



public int getCode() {
	return code;
}



public void setCode(int code) {
	this.code = code;
}



public int getCc() {
	return cc;
}



public void setCc(int cc) {
	this.cc = cc;
}



public int getCf() {
	return cf;
}



public void setCf(int cf) {
	this.cf = cf;
}






}
